  Map<String, dynamic> getChainData(String chainName) {
    String rpcUrl;
    String apiUrl;
    dynamic chainId;

    switch (chainName) {
      case 'ethereumMainnet':
        rpcUrl = 'https://eth.meowrpc.com';
        chainId = 1;
        apiUrl = 'https://api.etherscan.io/';
        break;
      case 'ethereumTestnet':
        rpcUrl = '';
        chainId = '';
        apiUrl = 'https://api-testnet.etherscan.io/';
        break;
      case 'bitcoinChain':
        rpcUrl = '';
        chainId = '';
        apiUrl = '';
        break;
      case 'tronMainnet':
        rpcUrl = '';
        chainId = '';
        apiUrl = '';
        break;
      case 'tronTestnet':
        rpcUrl = '';
        chainId = '';
        apiUrl = '';
        break;
      case 'bscMainnet':
        rpcUrl = 'https://bsc-dataseed.binance.org/';
        chainId = 56;
        apiUrl = 'https://api.bscscan.com/';
        break;
      case 'bscTestnet':
        rpcUrl = 'https://data-seed-prebsc-1-s1.binance.org:8545/';
        chainId = 97;
        apiUrl = 'https://api-testnet.bscscan.com/';
        break;
      case 'polygonTestnet':
        rpcUrl = 'https://rpc-mumbai.maticvigil.com';
        chainId = 80001;
        apiUrl = 'https://api-testnet.polygonscan.com/';
        break;
      case 'polygonMainnet':
        rpcUrl = 'https://polygon-rpc.com';
        chainId = 137;
        apiUrl = 'https://api.polygonscan.com/';
        break;
      default:
        throw Exception('Invalid chain');
    }
    return {
      'rpcUrl': rpcUrl,
      'chainId': chainId,
      'apiUrl': apiUrl
    };
  }

